$(function () {
    var video_id = $("#lakitaid").val();
    $.ajax({
        type: "POST",
        url: 'player/index',
        data: {
            video_id: video_id,
        },
        dataType: "text",
        beforeSend: function (xhr)
        {
            xhr.setRequestHeader("Ajax-Request", "true");
        },
        success: function (response)
        {
            console.log(response);
            var rs = response.split(":");
            console.log(rs[0]);
            if (response == '')
            {
                alert('Video không tồn tại')
                return;
            }

            jwplayer('mediaspace').setup({
                file: response,
                autostart: 'true',
                width: "100%",
                aspectratio: "16:9",
                stretching: 'fill',
                abouttext: "Lakita.vn",
                aboutlink: "https://lakita.vn",
                events: {
                    
                }
            });
        }
    });
});
